mapper.select:
select * from sys_dict_info


		id = #{id}


		dict_value = #{dictValue}


		dict_prompt like concat('%',#{dictPrompt},'%')


		access_level = #{accessLevel}


		order_id = #{orderId}


		remark like concat('%',#{remark},'%')




		order by ${sidx} ${order}


		order by id desc



	limit #{offset}, #{limit}

mapper.update:
update sys_dict_info
`dict_prompt` = #{dictPrompt}, `access_level` = #{accessLevel}, `order_id` = #{orderId}, `remark` = #{remark},
where id = #{id} and dict_value = #{dictValue}
